import {
    create,
    classes,
    append,
} from 'tiny-svg';

import BaseCanvas from 'diagram-js/lib/core/Canvas';


export class Canvas extends BaseCanvas {

    constructor (config, eventBus, graphicsFactory, elementRegistry) {
        super(config, eventBus, graphicsFactory, elementRegistry);
        this.eventBus = eventBus;
        this._uiGroup = this.createUiGroup();
        this._defs = this.createDefs();
    }

    getElementRegistry () {
        return this._elementRegistry;
    }

    getGraphicsFactory () {
        return this._graphicsFactory;
    }

    getChildren () {
        const elements = this._elementRegistry._elements;
        return Object.values(elements).filter((object) => {
            return object.element.id !== '__implicitroot';
        });
    }

    getElements () {
        return this.getChildren().map((object) => object.element);
    }

    createDefs () {
        const defs = create('defs');
        append(this._svg, defs);
        return defs;
    }

    createUiGroup () {
        const group = create('g');
        classes(group).add('ui');
        append(this._svg, group);
        return group;
    }

    getCurrentScale () {
        return +this._viewport.getCTM().a.toFixed(5);
    }

    addShape (element, parent, parentIndex) {
        const shape = super.addShape(element, parent, parentIndex);
        if (element.type === 'shape') {
            this.eventBus.fire('resize.element', { element });
        }
        return shape;
    }

    addUiElement (type, element) {
        this._ensureValid(type, element);

        const parent = this.getRootElement();

        this.eventBus.fire(type + '.add', { element, parent });

        const gfx = this.graphicsFactory.create(type, element);

        this.graphicsFactory.update(type, element, gfx);

        this.eventBus.fire(type + '.added', { element, gfx });

        return element;
    }

}

import { Behavior } from '../../eventBus/Behavior';


export class RedoBehavior extends Behavior {

    constructor (commandStack) {
        super();
        this.commandStack = commandStack;
    }

    during (event) {
        this.commandStack.redo();
    }

}

import { Behavior } from '../../eventBus/Behavior';


export class UndoBehavior extends Behavior {

    constructor (commandStack) {
        super();
        this.commandStack = commandStack;
    }

    during (event) {
        this.commandStack.undo();
    }

}

import CommandHandler from 'diagram-js/lib/command/CommandHandler';


/**
 * @abstract
 */
export class Command extends CommandHandler {

    constructor () {
        super();
    }

    /**
     * @param {Object} context
     * @return {Boolean}
     */
    canExecute (context) {
        return true;
    }

    /**
     * @param {Object} context
     */
    preExecute (context) { }

    /**
     * @abstract
     * @param {Object} context
     */
    execute (context) { }

    /**
     * @abstract
     * @param {Object} context
     */
    revert (context) { }

    /**
     * @param {Object} context
     */
    postExecute (context) { }

}

import { RedoBehavior } from './behaviors/RedoBehavior';
import { UndoBehavior } from './behaviors/UndoBehavior';
import { CommandStack } from './providers/CommandStack';


export default {
    __depends__: [],
    __init__: [
        'commandStack',
    ],
    __behaviors__: [
        [ 'command.undo', UndoBehavior ],
        [ 'command.redo', RedoBehavior ],
    ],

    commandStack: [ 'type', CommandStack ],
};

import DiagramJsCommandStack from 'diagram-js/lib/command/CommandStack';


export class CommandStack extends DiagramJsCommandStack {

    constructor (eventBus, injector) {
        super(eventBus, injector);
    }

    execute (command, context) {
        if (!command) {
            throw new Error('command required');
        }

        const action = { command, context, handler: this._getHandler(command) };

        if (this.canExecute(command, context, action)) {
            this._pushAction(action);
            this._internalExecute(action);
            this._popAction(action);
        }
    }

    canExecute (command, context, action) {
        if (!action) {
            action = { command, context, handler: this._getHandler(command) };
        }

        let result = this._fire(command, 'canExecute', action);

        if (result === undefined) {
            if (!action.handler) {
                return false;
            }

            if (action.handler.canExecute) {
                result = action.handler.canExecute(context);
            }
        }

        return result;
    }

    _internalUndo (action) {
        const command = action.command;
        const context = action.context;
        const handler = action.handler;

        this._atomicDo(() => {
            this._fire(command, 'revert', action);

            if (handler.revert) {
                this._markDirty(handler.revert(context));
            }

            this._revertedAction(action);
            this._fire(command, 'reverted', action);
        });
    }

    _internalExecute (action, redo) {
        const command = action.command;
        const context = action.context;
        const handler = action.handler;

        if (!handler) {
            const e = 'no command handler registered for <' + command + '>';
            throw new Error(e);
        }

        this._pushAction(action);

        if (!redo) {
            this._fire(command, 'preExecute', action);

            if (handler.preExecute) {
                handler.preExecute(context);
            }

            this._fire(command, 'preExecuted', action);
        }

        this._atomicDo(() => {
            this._fire(command, 'execute', action);

            if (handler.execute) {
                this._markDirty(handler.execute(context));
            }

            this._executedAction(action, redo);
            this._fire(command, 'executed', action);
        });

        if (!redo) {
            this._fire(command, 'postExecute', action);

            if (handler.postExecute) {
                handler.postExecute(context);
            }

            this._fire(command, 'postExecuted', action);
        }

        this._popAction(action);
    }

    registerHandler (command, handlerCls) {
        this._setHandler(command, handlerCls);
    }

    _getHandler (command) {
        return this._injector.instantiate(this._handlerMap[command]);
    }

}

import BaseElementRegistry from 'diagram-js/lib/core/ElementRegistry';


export class ElementRegistry extends BaseElementRegistry {

    constructor (props) {
        super(props);
    }

    /**
     * TODO: Remove hotfix for handles
     * @return {*}[]
     */
    getAll () {
        return super.getAll().filter((element) => element.type !== 'handle');
    }

}

/**
 * @abstract
 */
export class Behavior {

    /**
     * @param {Object} event
     */
    before (event) { }

    /**
     * @param {Object} event
     */
    during (event) { }

    /**
     * @param {Object} event
     */
    after (event) { }

}

import EventBus from './providers/EventBus';


export default {
    __depends__: [ ],
    __init__: [
        'eventBus',
    ],

    eventBus: [ 'type', EventBus ],
};

import DiagramJsEventBus from 'diagram-js/lib/core/EventBus';


export default class EventBus extends DiagramJsEventBus {

    constructor () {
        super();
    }

    fire (name, payload, middleware) {
        const breadcrumbs = name.split('.');
        const fireMiddleware = middleware || breadcrumbs.length === 2;
        const type = breadcrumbs.splice(0, 2).join('.');

        if (fireMiddleware) {
            super.fire(type + '.start', payload);
        }

        const result = super.fire(name, payload);

        if (fireMiddleware) {
            super.fire(type + '.end', payload);
        }

        return result;
    }

    registerBehavior (name, priority, behavior) {
        const breadcrumbs = name.split('.');

        const prototype = Object.getPrototypeOf(behavior);
        const methods = Object.getOwnPropertyNames(prototype).filter((p) => {
            return p !== 'constructor'
                && p[0] !== '_'
                && typeof behavior[p] === 'function';
        });

        const type = breadcrumbs.splice(0, 2).join('.');
        methods.forEach((method) => {
            const callback = behavior[method].bind(behavior);

            switch (method) {
                case 'before':
                    this.on(type + '.start', callback);
                    break;
                case 'during':
                    this.on(type, callback);
                    break;
                case 'after':
                    this.on(type + '.end', callback);
                    break;
                default:
                    this.on(type + '.' + method, callback);
            }
        });
    }

}

import { ElementRegistry } from './ElementRegistry';
import { Canvas } from './Canvas';
import EventBusModule from './eventBus';
import CommandStackModule from './command';
import PolicyModule from './policy';
import ToolboxModule from './toolbox';


export default {
    __depends__: [
        EventBusModule,
        CommandStackModule,
        ToolboxModule,
        PolicyModule,
    ],
    __init__: [
        'canvas',
        'elementRegistry',
    ],

    canvas: [ 'type', Canvas ],
    elementRegistry: [ 'type', ElementRegistry ],
};

import { Injector as DiDiInjector } from 'didi';
import deprecated from './deprecated';

const PRIORITY_DEFAULT = 1000;

let result = [];
let providers = [];
let commands = [];
let behaviors = [];
let rules = [];
let tools = [];

const bootstrap = (modules) => {
    const visited = (module) => result.indexOf(module) !== -1;

    const visit = (module) => {
        if (visited(module)) return;

        (module.__depends__ || []).forEach(visit);

        if (visited(module)) return;

        result.push(module);

        providers = providers.concat(module.__init__ || []);
        commands = commands.concat(module.__commands__ || []);
        behaviors = behaviors.concat(module.__behaviors__ || []);
        rules = rules.concat(module.__rules__ || []);
        tools = tools.concat(module.__tools__ || []);
    };

    modules.forEach(visit);

    return result;
};

export class Injector extends DiDiInjector {

    constructor (modules) {
        super(bootstrap(modules));
        this.copy();
        this.initialize();
        this.clear();
    }

    copy () {
        this.result = result;
        this.providers = providers;
        this.commands = commands;
        this.behaviors = behaviors;
        this.rules = rules;
        this.tools = tools;
    }

    initialize () {
        this.providers.forEach(this.initProvider.bind(this));
        this.commands.forEach(this.initCommand.bind(this));
        this.behaviors.forEach(this.initBehavior.bind(this));
        this.rules.forEach(this.initRule.bind(this));
        this.tools.forEach(this.initTool.bind(this));
    }

    load () {
        result = this.result;
        providers = this.providers;
        commands = this.commands;
        behaviors = this.behaviors;
        rules = this.rules;
        tools = this.tools;
    }

    clear () {
        result = [ ];
        providers = [ ];
        commands = [ ];
        behaviors = [ ];
        rules = [ ];
        tools = [ ];
    }

    loadModule (module) {
        this.load();
        bootstrap([ module ]);
        this.copy();
        module.__depends__.forEach(this.registerProviders.bind(this));
        this.registerProviders(module);
        this.initialize();
        this.clear();
    }

    registerProviders (module) {
        Object.keys(module).forEach((name) => {
            const type = module[name][0];
            const value = module[name][1];
            const factoryMap = {
                factory: this.invoke, type: this.instantiate, value: (v) => v,
            };
            const annotate = (...args) => {
                if (args.length === 1 && Array.isArray(args[0])) args = args[0];
                const fn = args.pop();
                fn.$inject = args;
                return fn;
            };
            const arrayUnwrap = (type, value) => {
                if (type !== 'value' && Array.isArray(value)) {
                    return annotate(value.slice());
                }
                return value;
            };

            this._providers[name] = [
                factoryMap[type],
                arrayUnwrap(type, value),
                type,
            ];
        });
    }

    initProvider (component) {
        try {
            if (typeof component === 'string') {
                if (deprecated.providers.indexOf(component) >= 0) {
                    const warning = 'You are using a deprecated provider: ';
                    console.warn(warning, component);
                }

                this.get(component);
            } else {
                this.invoke(component);
            }
        } catch (e) {
            console.error('Failed to instantiate component', component);
        }
    }

    initCommand (command) {
        command[1].$inject = undefined;
        this.get('commandStack').registerHandler(command[0], command[1]);
    }

    initBehavior (behavior) {
        if (behavior.length < 3) {
            behavior[2] = behavior[1];
            behavior[1] = PRIORITY_DEFAULT;
        }

        behavior[2].$inject = undefined;

        this.get('eventBus').registerBehavior(
            behavior[0],
            behavior[1],
            this.instantiate(behavior[2])
        );
    }

    initRule (rule) {
        if (rule.length < 3) {
            rule[2] = rule[1];
            rule[1] = PRIORITY_DEFAULT;
        }

        rule[2].$inject = undefined;
        this.get('rulePolicy').registerRule(
            rule[0],
            rule[1],
            this.instantiate(rule[2])
        );
    }

    initTool (tool) {
        tool[1].$inject = undefined;
        this.get('toolbox').registerTool(tool[0], this.instantiate(tool[1]));
    }

}

import { RulePolicy } from './providers/RulePolicy';


export default {
    __depends__: [ ],
    __init__: [
        'rulePolicy',
    ],
    rulePolicy: [ 'type', RulePolicy ],
};

export class RulePolicy {

    constructor () {
        this.rules = { };
    }

    registerRule (name, priority, rule) {
        if (!this.rules[name]) {
            this.rules[name] = [];
        }

        if (!rule.validate) {
            rule = { validate: rule };
        }

        this.rules[name].push(rule);

        // TODO sort by priority
    }

    /**
     *
     * @param {String} command
     * @param {Object} context
     * @return {Boolean}
     */
    allowed (command, context) {
        if (!this.rules[command]) {
            return true;
        }

        return this.rules[command].every((rule) => rule.validate(context));
    }

}

/**
 * @abstract
 */
export class Rule {

    /**
     * @abstract
     * @param {Object} context
     */
    validate (context) { }

}

import { Behavior } from '../../eventBus/Behavior';


export class AttachBehavior extends Behavior {

    constructor (toolbox) {
        super();
        this.toolbox = toolbox;
    }

    after () {
        this.toolbox.bindListeners();
    }

}

import { Behavior } from '../../eventBus/Behavior';


export class DetachBehavior extends Behavior {

    constructor (toolbox) {
        super();
        this.toolbox = toolbox;
    }

    before () {
        this.toolbox.activateDefault();
        this.toolbox.unbindListeners();
    }

}

import { Behavior } from '../../eventBus/Behavior';


export class DoubleClickBehavior extends Behavior {

    constructor (toolbox) {
        super();
        this.toolbox = toolbox;
    }

    during (event) {
        this.toolbox.onDoubleClick(event);
    }

}

import { Behavior } from '../../eventBus/Behavior';


export class HoverBehavior extends Behavior {

    constructor (toolbox) {
        super();
        this.toolbox = toolbox;
    }

    during (event) {
        this.toolbox.hover = event.element;
        this.toolbox.hoverGfx = event.gfx;
    }

    after (event) {
        this.toolbox.onHover(event);
    }

}

import { Behavior } from '../../eventBus/Behavior';


export class KeypressBehavior extends Behavior {

    constructor (toolbox) {
        super();
        this.toolbox = toolbox;
    }

    after (event) {
        switch (event.originalEvent.key) {
            case 'Esc': // IE/Edge specific value
            case 'Escape':
                this.toolbox.activateDefault();
                break;
            default:
                return;
        }

        event.originalEvent.preventDefault();
    }

}

import { Behavior } from '../../eventBus/Behavior';


export class OutBehavior extends Behavior {

    constructor (toolbox) {
        super();
        this.toolbox = toolbox;
    }

    during (event) {
        this.toolbox.hover = null;
        this.toolbox.hoverGfx = null;
    }

    after (event) {
        this.toolbox.onOut(event);
    }

}

import { Behavior } from '../../eventBus/Behavior';


export class PreviousToolBehavior extends Behavior {

    constructor (toolbox) {
        super();
        this.toolbox = toolbox;
    }

    during (event) {
        this.toolbox.activatePrevious();
    }

}

import { Behavior } from '../../eventBus/Behavior';


export class SnappedBehavior extends Behavior {

    constructor (toolbox) {
        super();
        this.toolbox = toolbox;
    }

    during (event) {
        if (event.snapped) {
            this.toolbox.snapped = event.snapped;
        }
    }

}

import { Behavior } from '../../eventBus/Behavior';


export class ToolboxActivateBehavior extends Behavior {

    constructor (toolbox) {
        super();
        this.toolbox = toolbox;
    }

    during (event) {
        this.toolbox.activate(event.tool);
    }

}

import { Behavior } from '../../eventBus/Behavior';


export class ToolManagerBehavior extends Behavior {

    constructor (toolbox) {
        super();
        this.toolbox = toolbox;
    }

    after (event) {
        this.toolbox.activate(event.tool);
    }

}

import InteractionEventModule from 'diagram-js/lib/features/interaction-events';

import { DoubleClickBehavior } from './behaviors/DoubleClickBehavior';
import { HoverBehavior } from './behaviors/HoverBehavior';
import { AttachBehavior } from './behaviors/AttachBehavior';
import { DetachBehavior } from './behaviors/DetachBehavior';
import { OutBehavior } from './behaviors/OutBehavior';
import { KeypressBehavior } from './behaviors/KeypressBehavior';
import { PreviousToolBehavior } from './behaviors/PreviousToolBehavior';
import { SnappedBehavior } from './behaviors/SnappedBehavior';
import { ToolboxActivateBehavior } from './behaviors/ToolboxActivateBehavior';
import { Toolbox } from './providers/Toolbox';
import { ToolManagerBehavior } from './behaviors/ToolManagerBehavior';


export default {
    __depends__: [
        InteractionEventModule,
    ],
    __init__: [
        'toolbox',
    ],
    __behaviors__: [
        [ 'attach', AttachBehavior ],
        [ 'detach', DetachBehavior ],
        [ 'element.hover', HoverBehavior ],
        [ 'element.out', OutBehavior ],
        [ 'element.dblclick', DoubleClickBehavior ],
        [ 'keypress', KeypressBehavior ],
        [ 'tool-manager.update', ToolManagerBehavior ],
        [ 'toolbox.previous', PreviousToolBehavior ],
        [ 'toolbox.activate', ToolboxActivateBehavior ],
        [ 'snapping.snapped', SnappedBehavior ],
    ],
    toolbox: [ 'type', Toolbox ],
};

import { event } from 'min-dom';


export class Toolbox {

    constructor (eventBus, canvas) {
        this.eventBus = eventBus;
        this.canvas = canvas;

        this.tools = { };
        this.activeTool = null;
        this.defaultTool = null;
        this.previousTool = null;
        this.activeContext = null;

        this.hover = null;
        this.hoverGfx = null;
        this.start = { x: 0, y: 0 };
        this.snapped = { };
        this.mouseDown = false;
        this.mouseEvent = { };
        this.pos = { x: 0, y: 0 };

        this.mouseDownListener = null;
        this.mouseMoveListener = null;
        this.mouseUpListener = null;
    }

    bindListeners () {
        this.mouseDownListener = this.onMouseDown.bind(this);
        this.mouseMoveListener = this.onMouseMove.bind(this);
        this.mouseUpListener = this.onMouseUp.bind(this);
        event.bind(document, 'mousedown', this.mouseDownListener, true);
        event.bind(document, 'mousemove', this.mouseMoveListener, true);
        event.bind(document, 'mouseup', this.mouseUpListener, true);
    }

    unbindListeners () {
        event.unbind(document, 'mousedown', this.mouseDownListener, true);
        event.unbind(document, 'mousemove', this.mouseMoveListener, true);
        event.unbind(document, 'mouseup', this.mouseUpListener, true);
        this.mouseDownListener = null;
        this.mouseMoveListener = null;
        this.mouseUpListener = null;
    }

    setDefaultTool (name) {
        this.defaultTool = name;
    }

    registerTool (name, tool) {
        this.tools[name] = tool;
        this.tools[name].type = name;
    }

    activate (name, context = {}) {
        if (this.activeTool) {
            this.activeTool.onDisable(context);
        }

        this.previousTool = this.activeTool;
        this.activeTool = this.tools[name];

        if (!this.activeTool) {
            return null;
        }

        this.activeContext = context;
        Object.assign(this.mouseEvent, context, {
            tool: this.activeTool,
            previousTool: this.previousTool,
        });
        this.eventBus.fire('toolbox.update', context);

        if (this.activeTool) {
            this.activeTool.onEnable(this.mouseEvent);
        }
        return this.activeTool;
    }

    activateDefault () {
        this.mouseDown = false;
        this.mouseEvent = { };

        this.activate(this.defaultTool);
    }

    activatePrevious (context) {
        if (this.previousTool) {
            this.activate(this.previousTool.type, context);
        } else {
            this.activate(this.defaultTool);
        }
    }

    onMouseDown (event = {}) {
        if (!this.activeTool) return;

        this.start = { };
        this.start.hover = this.hover;
        const point = this.toLocal({ x: event.clientX, y: event.clientY });
        this.start.x = point.x;
        this.start.y = point.y;
        this.snapped = { };
        this.mouseDown = true;
        this.mouseEvent = this.createMouseEvent(event);

        this.activeTool.onMouseDown(this.mouseEvent);
    }

    onMouseUp (event = {}) {
        if (!this.activeTool || !this.mouseDown) return;

        this.mouseDown = false;
        this.mouseEvent = this.createMouseEvent(event);

        this.activeTool.onMouseUp(this.mouseEvent);
    }

    onMouseMove (event = {}) {
        if (!this.activeTool) return;

        this.mouseEvent = this.createMouseEvent(event);

        this.activeTool.onMouseMove(this.mouseEvent);
    }

    onHover (event = {}) {
        if (!this.activeTool) return;

        // TODO: add mouseEvent to hover and out events
        // const mouseEvent = this.createMouseEvent(event);
        this.activeTool.onHover(event);
    }

    onOut (event = {}) {
        if (!this.activeTool) return;

        // const mouseEvent = this.createMouseEvent(event);
        // mouseEvent.hover = event.element;
        this.activeTool.onOut(event);
    }

    onDoubleClick (event = {}) {
        if (!this.activeTool) return;

        if (!this.hover) {
            this.hover = event.element;
            this.hoverGfx = event.gfx;
        }
        const originalEvent = event.originalEvent || this.mouseEvent;
        this.mouseEvent = this.createMouseEvent(originalEvent);
        this.activeTool.onDoubleClick(this.mouseEvent);
    }

    createMouseEvent (event = {}) {
        const payload = this.toLocal({ x: event.clientX, y: event.clientY });
        payload.originalEvent = event;
        payload.hover = this.hover;
        payload.isOnCanvas = this.isOnCanvas(event);
        payload.hoverGfx = this.hoverGfx;
        payload.context = this.activeContext;
        payload.mouseDown = this.mouseDown;
        payload.root = this.isRootElement(this.hover);
        payload.tx = event.clientX - this.pos.x;
        payload.ty = event.clientY - this.pos.y;
        this.pos = { x: event.clientX, y: event.clientY };

        this.eventBus.fire('snapping.snap', payload);

        if (this.start) {
            payload.sx = this.start.x;
            payload.sy = this.start.y;
            payload.x = this.snapped.x || payload.x;
            payload.y = this.snapped.y || payload.y;
            payload.dx = payload.x - this.start.x;
            payload.dy = payload.y - this.start.y;
            payload.hoverStart = this.start.hover;
            payload.rootStart = this.isRootElement(this.start.hover);
        }

        return this.eventBus.createEvent(payload);
    }

    toLocal (position) {
        const viewbox = this.canvas.viewbox();
        const clientRect = this.canvas._container.getBoundingClientRect();

        return {
            x: viewbox.x + (position.x - clientRect.left) / viewbox.scale,
            y: viewbox.y + (position.y - clientRect.top) / viewbox.scale,
        };
    }

    isOnCanvas (event) {
        return !!event.hover;
    }

    isRootElement (element) {
        return element && element.id === this.canvas._rootElement.id;
    }

}

/**
 * @abstract
 */
export class Tool {

    constructor () {

    }

    /**
     * @abstract
     * @param {Object} event
     */
    onMouseDown (event) { }

    /**
     * @abstract
     * @param {Object} event
     */
    onMouseMove (event) { }

    /**
     * @abstract
     * @param {Object} event
     */
    onMouseUp (event) { }

    /**
     * @abstract
     * @param {Object} event
     */
    onEnable (event) { }

    /**
     * @abstract
     * @param {Object} event
     */
    onDisable (event) { }

    /**
     * @param {Object} event
     */
    onHover (event) { }

    /**
     * @param {Object} event
