import { SVGSerializer } from './providers/SVGSerializer';


export default {
    __depends__: [
        MetaFormalismModule,
    ],
    __init__: [
        'exporter',
        'jsonSerializer',
        'svgSerializer',
    ],
    __behaviors__: [
        [ 'export', 1500, ExportBehavior ],
        [ 'export.json', 1500, JsonExportBehavior ],
        [ 'export.svg', 1500, SVGExportBehavior ],
