        return this.factory.createShape(Object.assign({},
            { orientation },
            attributes
        ));
    }

    show (handle) {
        const position = handle.orientation.position();
        handle.x = position.x - Math.round(handle.width / 2);
        handle.y = position.y - Math.round(handle.height / 2);
