export class Geometry {

    static localToGlobal (shape, point) {
        return { x: shape.x + point.x, y: shape.y + point.y };
    }

    static distance (p0, p1) {
        return Math.sqrt(Math.pow(p1.x - p0.x, 2) + Math.pow(p1.y - p0.y, 2));
    }

    static intersect (p0, p1, p2, p3) {
        return this.intersectAt(p0.x, p0.y, p1.x, p1.y, p2.x, p2.y, p3.x, p3.y);
    }

    static intersectAt (x0, y0, x1, y1, x2, y2, x3, y3) {
        const Ax = x0 - x1; const Ay = y1 - y0;
        const Bx = x2 - x3; const By = y3 - y2;

        const delta = Ay * Bx - By * Ax;

        if (delta === 0) return false;

        const CA = Ax * y0 + Ay * x0;
        const CB = Bx * y2 + By * x2;

        const invertedDelta = 1 / delta;
        const p = {
            x: (Bx * CA - Ax * CB) * invertedDelta,
            y: (Ay * CB - By * CA) * invertedDelta,
        };

        const min = { x: Math.min(x2, x3), y: Math.min(y2, y3) };
        const max = { x: Math.max(x2, x3), y: Math.max(y2, y3) };

        if (Math.round(min.x) <= Math.round(p.x)
            && Math.round(min.y) <= Math.round(p.y)
            && Math.round(max.x) >= Math.round(p.x)
            && Math.round(max.y) >= Math.round(p.y)) {
            return p;
        }
        return false;
    }

    static intersectPolyline (p0, p1, poly) {
        const intersects = [];
        for (let i=0; i<poly.points.length-1; i++) {
            const p = this.intersect(p0, p1, poly.points[i], poly.points[i+1]);
            if (p) intersects.push(p);
        }

        if (intersects.length === 0) return false;
        else if (intersects.length === 1) return intersects[0];

        return this.closest(intersects, p0);
    }

    static closest (points, point) {
        return points.reduce((result, p) => {
            const d = Geometry.distance(point, p);
            return d < result.d ? { p: p, d: d } : result;
        }, { p: null, d: Infinity }).p;
    }

    static corners (r) {
        return {
            NW: { x: r.x, y: r.y },
            NE: { x: r.x + r.width, y: r.y },
            SW: { x: r.x, y: r.y + r.height },
            SE: { x: r.x + r.width, y: r.y + r.height },
        };
    }

    static intersectRectangle (p0, p1, r) {
        const c = this.corners(r);

        if (p0.x < c.NW.x) {
            return this.intersect(p0, p1, c.NW, c.SW) || (p0.y < c.SW.y
                ? this.intersect(p0, p1, c.NE, c.NW)
                : this.intersect(p0, p1, c.SE, c.SW));
        } else if (p0.x > c.NE.x) {
            return this.intersect(p0, p1, c.NE, c.SE) || (p0.y < c.NE.y
                ? this.intersect(p0, p1, c.NE, c.NW)
                : this.intersect(p0, p1, c.SE, c.SW));
        } else if (p0.y < c.NW.y) {
            return this.intersect(p0, p1, c.NE, c.NW);
        } else if (p0.y > c.SE.y) {
            return this.intersect(p0, p1, c.SE, c.SW);
        }

        return false;
    }

    static intersectEllipse (p0, p1, e) {
        const a = e.rx * e.rx;
        const b = e.ry * e.ry;

        const x0 = p0.x - e.cx;
        const y0 = p0.y - e.cy;
        const x1 = p1.x - e.cx;
        const y1 = p1.y - e.cy;

        const dx = (x1 - x0);
        const dy = (y1 - y0);

        const A = dx * dx / a + dy * dy / b;
        const B = 2 * (x0 * dx / a + y0 * dy / b);
        const C = x0 * x0 / a + y0 * y0 / b - 1;

        const d = B * B - 4 * A * C;

        if (d < 0) return false;

        const t = (-B - Math.sqrt(d)) / (2 * A);

        return { x: x0 + dx * t + e.cx, y: y0 + dy * t + e.cy };
    }

}

export class PathParser {

    constructor (path) {
        this.x = 0;
        this.y = 0;
        this.setStart();

        this.commands = PathParser.parse(path);
        this.pos = 0;

        this.controlPoint = { x: 0, y: 0 };
    }

    nextSegment () {
        const command = this.commands[this.pos++];

        if (!command) return false;

        switch (command.marker) {
            case 'M':
                this.moveTo(command.values[0], command.values[1]);
                this.setStart();
                return this.nextSegment();
            case 'm':
                this.move(command.values[0], command.values[1]);
                this.setStart();
                return this.nextSegment();
            case 'L':
                return this.lineTo(command.values[0], command.values[1]);
            case 'l':
                return this.line(command.values[0], command.values[1]);
            case 'H':
                return this.lineTo(command.values[0], this.y);
            case 'h':
                return this.lineTo(command.values[0], 0);
            case 'V':
                return this.lineTo(this.x, command.values[0]);
            case 'v':
                return this.lineTo(0, command.values[0]);
            case 'Z':
            case 'z':
                if (this.x === this.start.x && this.y === this.start.y) {
                    return this.nextSegment();
                }
                return this.lineTo(this.start.x, this.start.y);
            case 'C':
                return this.cubicCurveTo(
                    command.values[0], command.values[1],
                    command.values[2], command.values[3],
                    command.values[4], command.values[5]
                );
            case 'c':
                return this.cubicCurve(
                    command.values[0], command.values[1],
                    command.values[2], command.values[3],
                    command.values[4], command.values[5]
                );
            case 'S':
                return this.cubicCurveTo(
                    this.x + (this.x - this.controlPoint.x),
                    this.y + (this.y - this.controlPoint.y),
                    command.values[0], command.values[1],
                    command.values[2], command.values[3]
                );
            case 's':
                return this.cubicCurve(
                    this.x + (this.x - this.controlPoint.x),
                    this.y + (this.y - this.controlPoint.y),
                    command.values[0], command.values[1],
                    command.values[2], command.values[3]
                );
            case 'Q':
                return this.quadraticCurveTo(
                    command.values[0], command.values[1],
                    command.values[2], command.values[3]
                );
            case 'q':
                return this.quadraticCurve(
                    command.values[0], command.values[1],
                    command.values[2], command.values[3]
                );
            case 'T':
                return this.quadraticCurveTo(
                    this.x + (this.x - this.controlPoint.x),
                    this.y + (this.y - this.controlPoint.y),
                    command.values[0], command.values[1]
                );
            case 't':
                return this.quadraticCurve(
                    this.x + (this.x - this.controlPoint.x),
                    this.y + (this.y - this.controlPoint.y),
                    command.values[0], command.values[1]
                );
            case 'A':
            case 'a':
                // TODO
        }

        return null;
    }

    setStart () {
        this.start = { x: this.x, y: this.y };
    }

    move (dx, dy) {
        this.moveTo(this.x + dx, this.y + dy);
    }

    moveTo (x, y) {
        this.x = Number(x.toFixed(8));
        this.y = Number(y.toFixed(8));
    }

    line (dx, dy) {
        return this.lineTo(this.x + dx, this.y + dy);
    }

    lineTo (x, y) {
        const line = {
            type: 'line',
            src: { x: this.x, y: this.y },
            dest: { x: Number(x.toFixed(8)), y: Number(y.toFixed(8)) },
        };
        this.moveTo(x, y);
        return line;
    }

    cubicCurve (dx1, dy1, dx2, dy2, dx, dy) {
        return this.cubicCurveTo(
            this.x + dx1, this.y + dy1,
            this.x + dx2, this.y + dy2,
            this.x + dx, this.y + dy
        );
    }

    cubicCurveTo (x1, y1, x2, y2, x, y) {
        const curve = this.lineTo(x, y);
        curve.type = 'cubic';
        curve.bezier1 = { x: x1, y: y1 };
        curve.bezier2 = { x: x2, y: y2 };
        this.controlPoint = curve.bezier2;
        return curve;
    }

    quadraticCurve (dx1, dy1, dx, dy) {
        return this.quadraticCurveTo(
            this.x + dx1, this.y + dy1,
            this.x + dx, this.y + dy
        );
    }

    quadraticCurveTo (x1, y1, x, y) {
        const curve = this.lineTo(x, y);
        curve.type = 'quadratic';
        curve.bezier = { x: x1, y: y1 };
        this.controlPoint = curve.bezier;
        return curve;
    }

    /**
     * src: https://gist.github.com/shamansir/0ba30dc262d54d04cd7f79e03b281505
     *
     * @param {String} path
     * @return {{marker: (String), values: (Number[])}[]}
     */
    static parse (path) {
        const commands = /[MmLlHhVvZzCcSsQqTtAa]/g;
        const digits = /-?\d*\.?\d+/g;

        const results = [];

        let match;
        while ((match = commands.exec(path))) {
            results.push(match);
        }

        return results.map((match) => {
            return {
                marker: path[match.index],
                index: match.index,
            };
        }).reduceRight((all, match) => {
            const end = all.length ? all[all.length - 1].index : path.length;
            let chunk = path.substring(match.index, end);
            if (chunk.length > 0) {
                chunk = chunk.substr(1, chunk.length - 1);
            }
            return all.concat([
                {
                    marker: match.marker,
                    index: match.index,
                    chunk: chunk,
                },
            ]);
        }, []).reverse().map((command) => {
            const values = command.chunk.match(digits);
            return {
                marker: command.marker,
                values: values ? values.map(parseFloat) : [],
            };
        });
    }

}

import Diagram from 'diagram-js';

import CoreModule from './core';
import DrawModule from './draw';
import LayouterModule from './features/layouter';
import SelectionModule from './features/selection';
import ZoomModule from './features/zoom';
import ExportModule from './features/export';
import ImportModule from './features/import';

import { Injector } from './core/Injector';


export default class Viewer extends Diagram {

    constructor (options = { modules: [ ], canvas: { } }) {
        const container = document.createElement('div');
        container.id = options.canvas.id || 'rnw-viewer';
        container.className = 'rnw-container';

        options.canvas.container = container;

        const injector = new Injector([
            { 'config': [ 'value', options ] },
            CoreModule,
            DrawModule,
            ZoomModule,
            LayouterModule,
            SelectionModule,
            ExportModule,
            ImportModule,
        ].concat(options.modules));

        super(options, injector);

        this.injector = injector;
        this.container = container;
    }

    attachTo (parentNode) {
        this.detach();

        this.fire('attach.start', { instance: this, parentNode });

        parentNode.appendChild(this.container);

        this.get('canvas').resized();

        this.fire('attach.end', { instance: this, parentNode });
    }

    detach () {
        const parentNode = this.container.parentNode;

        if (!parentNode) {
            return;
        }

        this.fire('detach.start', { instance: this });

        parentNode.removeChild(this.container);

        this.fire('detach.end', { instance: this });
    }

    fire (eventName, payload, middleware) {
        this.get('eventBus').fire(eventName, payload, middleware);
    }

    on (eventName, callback) {
        this.get('eventBus').on(eventName, callback);
    }

    off (eventName, callback) {
        this.get('eventBus').off(eventName, callback);
    }

    addFormalism (plugin) {
        const pluginInstance = this.injector.instantiate(plugin);
        this.fire('plugin.register', {
            plugin: pluginInstance,
        });
    }

}

